mapper.select:
SELECT br.id AS repositoryId, br.product_id AS productId, br.department_id AS stationId, bp.name AS productName, sd.name AS stationName, br.current_quantity AS currentQuantity, COALESCE(SUM(bo.quantity), 0) AS orderCount, COALESCE(SUM(bo.total_amount), 0) AS totalAmount, COALESCE(sd.commission_rate, 0) AS commissionRate FROM biz_repository br JOIN biz_products bp ON br.product_id = bp.id JOIN sys_dept sd ON br.department_id = sd.id LEFT JOIN biz_orders bo ON bo.repo_id = br.id
AND bo.order_date >= #{query.startDateTime}
AND bo.order_date <= #{query.endDateTime}
br.product_id = #{query.productId}
AND br.department_id = #{query.stationId}
GROUP BY br.id, br.product_id, br.department_id, bp.name, sd.name, br.current_quantity, sd.commission_rate ORDER BY bp.name, sd.name

SELECT br.id AS repositoryId, br.product_id AS productId, bp.name AS productName, br.department_id AS stationId, sd.name AS stationName, br.current_quantity AS currentQuantity, br.order_quantity_total AS orderQuantityTotal, br.order_date_latest AS orderDateLatest FROM biz_repository br JOIN biz_products bp ON br.product_id = bp.id JOIN sys_dept sd ON br.department_id = sd.id
br.product_id = #{query.productId}
AND br.department_id = #{query.stationId}
ORDER BY bp.name, sd.name
